import React from 'react';
import {Drawer,} from '@mui/material';
import DarkModeIcon from '@mui/icons-material/DarkMode';
import PersonIcon from '@mui/icons-material/Person';
import "./navdrawer.css";

type NavBarProps = {
    name?: string;
    children?: React.ReactNode;
    imageLink?: string;

};

/// <summary>
/// This is the NavBar component.
/// </summary>
export default function NavDrawer(props: NavBarProps) {
    const { name, children, imageLink } = props;
    // Create a condition that targets viewports at least 768px wide
    const mediaQuery = window.matchMedia('(min-width: 1000px)')

    function handleTabletChange(e : any) {
        // Check if the media query is true
        if (e.matches) {
            // If true, show the drawer
            setDrawerState(true);
        }
    }

    // Register event listener
    mediaQuery.addListener(handleTabletChange)

    // Initial check
    handleTabletChange(mediaQuery)
    return (
        <div id="navdraw">
            <Drawer
                variant="permanent"
                PaperProps={{ style: {
                    marginTop: 27,
                    marginLeft: 40,
                    marginBottom: 2,                        
                    background: "linear-gradient(0deg, rgba(103, 80, 164, 0.05), rgba(103, 80, 164, 0.05)), #FFFBFE",
                } }}
                ModalProps={{
                    keepMounted: true,
                }}>
                <div className="chattop chatprofile">
                    <div className="sp-Lockup">
                        <DarkModeIcon className="DMI"/>
                        <div className="Cursor"></div>
                        <PersonIcon className="PersonIcon" />
                        <img className="profile" width="75" alt="" src={imageLink}/>
                        <label className="product">{name}</label>
                    </div>                        
                </div>
                {children}          
            </Drawer>
        </div>
    );   
}
